
import requests
from bs4 import BeautifulSoup
import database


def get_leader_info(link_suffix):
    leader_url = f"https://www.schulichleaders.com/{link_suffix}"

    page = requests.get(leader_url, verify=False)
    soup = BeautifulSoup(page.content, 'html.parser')

    university_div = soup.find("div", class_="person--selected-by")
    university_name = university_div.text

    leader_heading = soup.find("h1", class_="person--title")
    leader_name = leader_heading.text

    year_heading = soup.find("h2", class_="person--selected-in")
    year = year_heading.text[:4]

    program_div = soup.find("div", class_="person--program")
    program_name = program_div.text

    information = (leader_url, leader_name, program_name, university_name, year)

    database.add_row(information)


def get_link_suffixes():
    page = requests.get(f"https://www.schulichleaders.com/scholars#slide-scholars", verify=False)
    soup = BeautifulSoup(page.content, 'html.parser')

    spans = soup.find_all("span", class_="field-content")
    anchors = [name.find("a") for name in spans]
    link_suffixes = [anchor.get("href") for anchor in anchors if anchor]

    return link_suffixes

